definition.script:
pipeline {    agent any    environment {		BUILD_NAME = "DMAP App Binary Build"		NOTIFYUSERS = 'pawant@newtglobalcorp.com'		BUILD_DETAILS = "<BR>Job Name: ${env.JOB_NAME} <BR>Build Number: ${env.BUILD_NUMBER} <BR>Build URL: ${BUILD_URL}"	}    parameters {        string(name: 'Java_Backend', defaultValue: 'develop', description: 'Enter the name of the Java Backend branch')        string(name: 'Flask_Backend_branch', defaultValue: 'develop', description: 'Enter the name of the Backend branch')	    string(name: 'Container_Name', defaultValue: '', description: 'Enter the name of the container in lower case, backup: you can put your name')        string(name: 'Build_Image', defaultValue: 'ngdmapo/app_build:v1', description: 'Enter the name of the docker build image which will be used to build binaries')        string(name: 'Service_pack_version', defaultValue: '', description: 'Enter the version of Service pack. Sample values will be like: 6.3.2.0')        string(name: 'Latest_dmap_image_version', defaultValue: '', description: 'Enter the latest version of DMAP image. Sample values be like:7.4.2.1')        string(name: 'Minimum_dmap_image_version', defaultValue: '', description: 'Enter the Minimum version of DMAP DB image to run current binaries. Sample values be like:7.4.2.1')        choice(name: 'Storage_Location', choices: ['AWS_S3', 'Azure_Blob','Both'], description: 'Enter the storage location to store Service pack which is either in S3 or BLOB')		string(name: 'S3_Bucket', defaultValue: '', description: 'Enter the S3 Bucket path for uploading the binaries. Sample values will be like dev: dmap-deployment/dev, qa: dmap-deployment/qa, prod: dmap-deployment/prod')		string(name: 'Blob_Storage', defaultValue: '', description: 'Enter the Blob storage path for uploading the binaries. Sample values will be like dev: dmapextndevreleases, qa: dmapextnqareleases, prod: dmapextnreleases')        string(name: 'sonarTypes', defaultValue: 'VULNERABILITY,BUG,CODE_SMELL', description: 'Enter the sonarQube issue types to certify the build')        string(name: 'sonarSeverities', defaultValue: 'CRITICAL,BLOCKER,MAJOR', description: 'Enter the sonarQube issue severities to certify the build')        choice(name: 'Build_Type', choices: ['Dev', 'QA', 'Prod'], description: 'Select build type')        choice(name: 'docker_image', choices: ['dmap_app_migration_azure_blob_dev','dmap_app_migration_azure_blob_qa','dmap_app_migration_azure_blob_prod','dmap_aws_s3_prod','dmap_aws_s3_dev', 'dmap_aws_s3_qa',], description: 'Select Docker Image name')	    string(name: 'docker_branch', defaultValue: 'develop', description: 'Enter git branch to pull docker files')        string(name: 'docker_image_version', defaultValue: 'v1.0.0.0', description: 'Enter the Docker Image version')        string(name: 'service_pack_location', defaultValue: '', description: 'Enter service pack location (S3/BLOB). It should be full path')        }    options {        timestamps()        timeout(time: 5, unit: 'HOURS')        buildDiscarder(logRotator(numToKeepStr: '10'))    }    stages {        stage ("Checkout Code ") {			steps {			    			    dir("DMAP_Extension_Backend"){			        deleteDir()					echo "Checkout the DMAP_Extension code"					git branch: Java_Backend, credentialsId: 'Kavya_1709', url: 'https://github.com/newtglobalgit/DMAP_Extension-Backend.git'			            }			    dir("DMAP_Extension_flask"){			        deleteDir()					echo "Checkout the DMAP_Extension_flask code"					git branch: Flask_Backend_branch, credentialsId: 'Kavya_1709', url: 'https://github.com/newtglobalgit/DMAP_Extension-Flask.git'			             }                dir("DMAP_App_Docker"){			        deleteDir()					echo "Checkout the DMAP_DB_Dockerfile"					git branch: docker_branch, credentialsId: 'Kavya_1709', url: 'https://github.com/newtglobalgit/DMAP_Extension_Docker_Linux.git'			        } 			      }        }			stage ("Sonar Analysis") {			steps{			    echo "Flask sonar Analysis"			      dir("DMAP_Extension_flask"){			          sh "rm -f templates/pdfreportTemp"			     //sh "/opt/sonarqube/bin/linux-x86-64/sonar-scanner/bin/sonar-scanner \                 //-Dsonar.projectKey=dmap_extension_flask \                 //-Dsonar.sources=. \                 //-Dsonar.host.url=http://localhost:9000 \                 //-Dsonar.login=beee7dcd5a37e60e6a205ebc16b49169e476f5b2" 				  }				echo "Java sonar Analysis"			      dir("DMAP_Extension_Backend"){                            //sh "mvn clean verify sonar:sonar \                //-Dsonar.projectKey=DMAP-Extension-Backend \                //-Dsonar.host.url=http://localhost:9000 \                //-Dsonar.login=b761082428934a5eba13f9daec6eb91e5a82ffff"            }			    			}     					    }		stage ("Java Backend Tests"){			steps{			    dir("DMAP_Extension_Backend"){				    echo "Started Snyk Tests"				    				   sh "chmod +x mvnw"			         snykSecurity failOnIssues: false, snykInstallation: 'snyk', snykTokenId: 'Snyk_API'								    													//     //publishHTML([allowMissing: false, alwaysLinkToLastBuild: false, keepAll: false, reportDir: 'SonarQube', reportFiles: 'JavaSonarQualityGateCheck.html', reportName: //'Java_Backend SonarQube Analysis Result', reportTitles: 'SonarQube Result'])				     publishHTML([allowMissing: false, alwaysLinkToLastBuild: false, keepAll: false, reportDir: 'DMAP_Extension_Backend', reportFiles: 'snyk_report.html', reportName: 'Snyk Result', reportTitles: 'Snyk Result'])			     }			}		}		stage ("Java Backend Build"){			steps{			    withCredentials([string(credentialsId: 'GIT_User', variable: 'GITUser'),string(credentialsId: 'Git_New_PAT', variable: 'Password')]) {                    sh "docker run -d -it -e GIT_USER=${GITUser} -e GIT_PASS=${Password} -e GIT_BRANCH=${params.Java_Backend} --name=${params.Container_Name} ngdmapo/java_17:v1"                }			    dir("DMAP_Extension_Backend"){				    sh "docker ps -a"					sh "docker cp BuildScript/make_backend.sh ${params.Container_Name}:/opt/app-root/src"					echo "------------Copied build scipt inside build container------------------"					sh "docker exec ${params.Container_Name} sh /opt/app-root/src/make_backend.sh"					echo "----------------------------Binaries created-------------------------------"					sh "docker cp ${params.Container_Name}:/opt/app-root/src/DMAP_Extension-Backend/target/DMAP.tar ."					echo "------------------------Copied binaries in Jenkins----------------------"					sh "cp DMAP.tar ../"			     			      }			      	echo "------------------Removing Build container ------------------"					sh "docker stop ${params.Container_Name}"			    sh "docker rm -f ${params.Container_Name}"			}		}		stage ("Flask Backend Test"){			steps{			    dir("DMAP_Extension_flask"){				    echo "Running Bandit"				    				   sh "bandit -r ./ -x ./Deploy -ll -f html -o AppBanditAnalysisReport.html || true"            	publishHTML([allowMissing: false, alwaysLinkToLastBuild: false, keepAll: true, reportDir: '.', reportFiles: 'AppBanditAnalysisReport.html', reportName: 'App Bandit Security Report', reportTitles: 'App Bandit Security Report'])	     			      }			}		}		stage ("Flask Backend Build"){			steps{				    echo "Create Binaries"                   sh "docker pull ngdmapo/app_build:v1"                 sh "docker run -it -d -p 5009:5002 -p 9090:8080 --name=${params.Container_Name} ngdmapo/app_build:v1"				    echo "---------------------Completed creating build container---------------------------"                    dir("DMAP_Extension_flask"){                    echo "------------------Build container created-------------------------------"					sh "docker ps -a"                    sh "docker cp . ${params.Container_Name}:/usr/local/tomcat/DMAP_Extension-Flask"					sh "docker cp BuildScript/make_backend.sh ${params.Container_Name}:/usr/local/tomcat"					echo "------------Copied build scipt inside build container------------------"					sh "docker exec --user=root ${params.Container_Name} sh /usr/local/tomcat/make_backend.sh"					echo "----------------------------Binaries created-------------------------------"					sh "docker cp ${params.Container_Name}:/usr/local/tomcat/DMAP_Extension-Flask/dist/Service.tar ."					echo "------------------------Copied binaries in Jenkins----------------------"					sh "cp Service.tar ../"			      }                echo "------------------Removing Build container ------------------"				sh "docker stop ${params.Container_Name}"			    sh "docker rm -f ${params.Container_Name}"			    echo "------------------Build container removed successfully------------------"			}		}		        stage ("Package Binary"){			steps {			    echo "------------------Package Binary------------------"			    script {			      def now = new Date()			      def time = now.format("yyyy-MM-dd HH:mm:ss", TimeZone.getTimeZone('UTC'))			      writeFile(file: 'dmap_release.yaml', text:"product_name: DMAP
binary_version: ${params.Service_pack_version}
minimal_supported_image_version: ${params.Minimum_dmap_image_version}
date_published: ${time}
build_type: ${params.Build_Type}")			      sh "tar cvf dmap_release.tar DMAP.tar Service.tar dmap_release.yaml"			    }			}		}        stage ("Blob Storage Upload"){		    when {                expression { params.Storage_Location == "Azure_Blob" || params.Storage_Location == "Both" }            }			steps {				echo "---------------Deploying Artifacts to Blob Storage--------------"				script {				    echo "Azure Blob Service Pack"				    def now = new Date()			        def time = now.format("yyyy-MM-dd HH:mm:ss", TimeZone.getTimeZone('UTC'))										sh "echo \$(md5sum dmap_release.tar | cut -d ' ' -f1) >> dmap_tar_checksum.txt"								        sh "echo \$(stat -c "%n,%s" dmap_release.tar | column -s, | cut -d ',' -f2) >> dmap_tar_filesize.txt"								        def md5sum = readFile(file: 'dmap_tar_checksum.txt')			        def filesize = readFile(file: 'dmap_tar_filesize.txt')										sh "rm -rf dmap_tar_checksum.txt"			        sh "rm -rf dmap_tar_filesize.txt"										sh "echo latest_release_info"			        writeFile(file: 'dmap_latest_release_info.yaml', text:"product_name: DMAP
latest_image_version: ${params.Latest_dmap_image_version}
latest_binary_version: ${params.Service_pack_version}
minimal_supported_image_version: ${params.Minimum_dmap_image_version}
date_published: ${time}
checksum: ${md5sum}filesize: ${filesize}url_to_download_dmap_binary: https://msmkdmapdisks.blob.core.windows.net/${params.Blob_Storage}/")				}							        withCredentials([string(credentialsId: 'BlobAccountKey', variable: 'BlobAccountKey')]) {                        sh "az storage blob upload --account-name msmkdmapdisks --account-key ${BlobAccountKey} --container-name ${params.Blob_Storage} --file dmap_release.tar --name dmap_release.tar"                        sh "az storage blob upload --account-name msmkdmapdisks --account-key ${BlobAccountKey} --container-name ${params.Blob_Storage} --file dmap_latest_release_info.yaml --name dmap_latest_release_info.yaml"			        }                			}		}        	stage ("Docker Build") {    		steps {    		    dir("DMAP_App_Docker"){    		    script {    		    writeFile(file: 'dmap_image_release.yaml', text:"name: DMAP
image_version: ${params.docker_image_version}
initial_service_pack_version: ${params.Service_pack_version}
")			    if(params.docker_image == "dmap_aws_s3_dev" || params.docker_image == "dmap_aws_s3_qa" || params.docker_image == "dmap_aws_s3_prod"){       			        echo "Build AWS Docker Image"    			                        sh """                    sed -i -e 's#service_pack_location#${params.service_pack_location}#' docker_dmap_aws                    """                        			    sh "docker build -t ngdmapo/${params.docker_image}:${params.docker_image_version} -f docker_dmap_aws ."    			    sh "docker images"			    }			    else if(params.docker_image == "dmap_app_migration_azure_blob_prod" || params.docker_image == "dmap_app_migration_azure_blob_dev" || params.docker_image == "dmap_app_migration_azure_blob_qa"){			        echo "Docker Azure Blob"			        writeFile(file: 'dmap_image_release.yaml', text:"name: DMAP
image_version: ${params.docker_image_version}
initial_service_pack_version: ${params.Service_pack_version}
")			        			        echo "Build Azure Docker Image"    			    sh """                    sed -i -e 's#service_pack_location#${params.service_pack_location}#' Dockerfile                    """                        			    sh "docker build -t ngdmapo/${params.docker_image}:${params.docker_image_version} ."    		    			 			    }			     sh "docker images"    		    }			    }    		}    		    	} 	    stage ("Docker Push") {    		steps {    		   script{    		        echo "Push Docker Image"     			    withCredentials([usernamePassword(credentialsId: 'DockerHubCred', passwordVariable: 'dockerHubPassword', usernameVariable: 'dockerHubUser')]) {                    //    sh "docker login -u ${env.dockerHubUser} -p ${env.dockerHubPassword}"    			        //sh "docker tag ngdmapo/${docker_image}:${docker_image_version} ngdmapo/${docker_image}"    			   //     sh "docker push ngdmapo/test_app:v1"    			        //sh "docker push ngdmapo/${docker_image}"    			          			                            }    			        			    }    	    	    }	    }	    stage ("Remove Docker Image") {    		steps {    			    echo "Remove Docker Image"    			    //sh "docker rmi ngdmapo/${params.docker_image}:${docker_image_version}"    		}    		    	}    }    post {        always {            emailext attachmentsPattern: 'DMAP_Extension_flask/AppBanditAnalysisReport.html,DMAP_Extension_Backend/snyk_report.html',            subject: "Jenkins Job Report For ${BUILD_NAME} - ${currentBuild.currentResult}",			body: "BUILD DETAILS: ${BUILD_DETAILS} <BR> BUILD STATUS: ${currentBuild.currentResult}",			to: "${NOTIFYUSERS}"        }    }}
